import './App.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React from 'react';

let arr = [];

//guardar o input
//tenho de ir alterando o estado da minha lista de tarefas (acho eu)
function ButtonAdd() {
 const [list, setList] = React.useState("");



 const handleClick = () =>{
  let task = document.getElementById('tasks').value;
    setList(list===task);
    arr.push(task);

  }



  return (
    <>
    <div>
    <input type="text" id="tasks" placeholder="What needs to de done?" />
      <button onClick={handleClick} type='button'>
        Add
      </button>
    </div>

  </>


  )
}

function TabsTasks({array}){
  function NumberList(array) {
    console.log(array)
     const listItems = array.map((element) =>
     <li>{element}</li>
     );
     return (
       <ul>{listItems}</ul>
       );
   }

  return(
    <Tabs>
    <TabList>
      <Tab>All</Tab>
    </TabList>
    <TabPanel>
      {NumberList(array)}

    </TabPanel>
  </Tabs>
  )
}





function App() {
  return (
    <div className="App">
      <h1>TODO LIST</h1>
      <ButtonAdd />
      <TabsTasks array={arr}/>
    </div>
  );
}

export default App;
