import './App.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React from 'react';



function ButtonAdd() {
 const [input, setInput] = React.useState("");
 let answer = document.getElementById('tasks').value


 const handleClick = () =>{

    setInput(input === "" ? answer : "");
  }

  return (
    <>
    <div>
    <input type="text" id="tasks" placeholder="What needs to de done?" />
      <button onClick={handleClick} type='button'>
        Add
      </button>
    </div>
<div>
    <Tabs>
    <TabList>
      <Tab>All</Tab>
    </TabList>
    <TabPanel>
      <h2>{input}</h2>

    </TabPanel>
  </Tabs>
  </div>
  </>


  )
}





function App() {
  return (
    <div className="App">
      <h1>TODO LIST</h1>
      <ButtonAdd />
    </div>
  );
}

export default App;
