import './App.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React from 'react';

let nextId = 0;



//guardar o input
//tenho de ir alterando o estado da minha lista de tarefas (acho eu)
function ButtonAdd() {
  const [buttonText, setButtonText] = React.useState('Incomplete');
  const [task, setTask] = React.useState('');
  const [list, setList] = React.useState([]);

const foo = function (id) {
    task.id
    setButtonText(
      buttonText === 'Incomplete' ? 'Complete' : 'Incomplete')
  }

const handleClick = () => {
  setList([
    ...list,
    { id: nextId++,
      description: task,
      isComplete: false
    }
  ]);
}

return (
  <>
    <div>
      <input
        type="text"
        value={task}
        onChange={e => setTask(e.target.value)}
        placeholder="What needs to de done?"
      />

      <button
        onClick={handleClick}
        type='button'>
        Add
      </button>
    </div>
    <Tabs>
      <TabList>
        <Tab>All</Tab>
      </TabList>
      <TabPanel>
        <ul>
          {list.map(task => (
            <li key={task.id}>
              {task.description}
              <button onClick={() => {
                setList(
                  list.filter(a => a.id !== task.id)
                )
              }
              }>
                delete
              </button>
              <button onClick = {() => foo (task.id)
              }>
                {buttonText}
              </button>
            </li>
          ))}
        </ul>
      </TabPanel>
    </Tabs>

  </>


)
}


function App() {
  return (
    <div className="App">
      <h1>TODO LIST</h1>
      <ButtonAdd />
    </div>
  );
}

export default App;
