function ButtonAdd() {
 const [task, setTask] = React.useState('');
 const [list, setList] = React.useState([]);

 const handleClick = () =>{
    setList(list===task);
    arr.push(task);    
  }

  return (
    <>
    <div>
    <input 
    type="text"
    value = {task}
    onChange = {e => setTask(e.target.value)}
    placeholder="What needs to de done?"
    />

    <button
    onClick={handleClick}
    type='button'>
        Add
      </button>
    </div>
    <Tabs>
    <TabList>
      <Tab>All</Tab>
    </TabList>
    <TabPanel>
      {NumberList(array)}

    </TabPanel>
  </Tabs>

  </>


  )
}


function App() {
  return (
    <div className="App">
      <h1>TODO LIST</h1>
      <ButtonAdd />
      <TabsTasks array={arr}/>
    </div>
  );
}

export default App;
