import './App.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React from 'react';

let nextId = 0;



//guardar o input
//tenho de ir alterando o estado da minha lista de tarefas (acho eu)
function ButtonAdd() {
  function foo (){
    var x = document.getElementById("myDIV");
    if (x.innerHTML === "Hello") {
      x.innerHTML = "Swapped text!";
    } else {
      x.innerHTML = "Hello";
    }
  }

  const [task, setTask] = React.useState('');
  const [list, setList] = React.useState([]);

  const handleClick = () => {
    setList([
      ...list,
      { id: nextId++, task: task }
    ]);
  }

  return (
    <>
      <div>
        <input
          type="text"
          value={task}
          onChange={e => setTask(e.target.value)}
          placeholder="What needs to de done?"
        />

        <button
          onClick={handleClick}
          type='button'>
          Add
        </button>
      </div>
      <Tabs>
        <TabList>
          <Tab>All</Tab>
        </TabList>
        <TabPanel>
          <ul>
            {list.map(b => (
              <li key={b.id}>
                {b.task}
                <button onClick={() => {
                  setList(
                    list.filter(a => a.id !== b.id)
                  )
                }
                }>
                  delete
                </button>

              </li>
            ))}
          </ul>
        </TabPanel>
      </Tabs>

    </>


  )
}


function App() {
  return (
    <div className="App">
      <h1>TODO LIST</h1>
      <ButtonAdd />
    </div>
  );
}

export default App;
